import React, { useState } from "react";
import { connect } from "react-redux";
import {
  UserPhotoContainer,
  UserIconContainer,
  UserNameContainer,
  Name,
  UserContainer,
} from "./userIcon.style";
import { selectCurrentUser } from "../Redux/User/userSelector";
import { createStructuredSelector } from "reselect";
const UserIcon = ({ currentUser }) => {
  const [close, invert] = useState(false);
  if (currentUser) {
    return (
      <UserIconContainer>
        <UserPhotoContainer>
          <img
            src={currentUser.photoURL}
            alt="user photo"
            style={{ width: "100%", height: "100%" }}
          />
        </UserPhotoContainer>
        <UserNameContainer>
          <Name>{currentUser.displayName}</Name>
          <button onClick={() => invert(!close)}>
            <svg
              width="45"
              height="22"
              viewBox="0 0 45 22"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <line
                x1="8.66272"
                y1="7.25113"
                x2="23.6401"
                y2="20.5055"
                stroke="black"
                strokeWidth="2"
              />
              <line
                x1="22.2929"
                y1="20.5061"
                x2="36.435"
                y2="6.36395"
                stroke="black"
                strokeWidth="2"
              />
            </svg>
          </button>
        </UserNameContainer>
      </UserIconContainer>
    );
  } else {
    return <div></div>;
  }
};
const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
});
export default connect(mapStateToProps, null)(UserIcon);
