import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCollectionForPreview } from "../Redux/shop/shopSelector";

import {
  SearchContainer,
  SearchButtonContainer,
  SearchInputContainer,
  Icon,
} from "./Search.style";
import OutsideClickEvent from "../OutsideClickEvent/OutsideClickEvent";
const searchInputVariants = {
  initial: { width: "0", display: "none" },
  final: { width: "200px", display: "block" },
  transition: {},
};
const SearchContainerVariants = {
  initial: { width: "50px" },
  final: { width: "300px" },
};

const SearchUI = ({ SHOP_DATA }) => {
  console.log("shot data", SHOP_DATA);
  let arrayb = [];
  if (SHOP_DATA) {
    const modifiedItems = () =>
      SHOP_DATA.map((array) => {
        arrayb[array.routeName] = array.items.map((item) => item.name);
        return arrayb;
      });
    console.log("modified", modifiedItems());
  }
  const shapeData = (SHOP_DATA) => {
    let modifiedData = [];

    if (SHOP_DATA) {
      let modifiedItems = SHOP_DATA.map((array) =>
        array.items.map((item) => item.name)
      );
      console.log("modified", modifiedItems);
      SHOP_DATA.map(({ routeName, items }) => {});
      SHOP_DATA.reduce(
        (acc, array) =>
          (acc = [array.routeName, array.items.map((item) => item.name)]),
        []
      );
    }
  };
  console.log("modified array", shapeData());
  const [suggestions, setSuggestions] = useState([]);
  const onTextChange = (e, items) => {
    const value = e.target.value;
    if (value > 0) {
      const regex = new RegExp(`^${value}`, "i");
      setSuggestions(items.sort().filter((v) => regex.text(v)));
    }
  };
  const renderSuggestionsList = () => {
    if (suggestions.length > 0) {
      return (
        <ul>
          {suggestions.map((suggestion) => (
            <li>{suggestion.name}</li>
          ))}
        </ul>
      );
    }
    return null;
  };
  const [clicked, setClicked] = useState(false);
  const ref = useRef();
  OutsideClickEvent(ref, () => setClicked(false));
  return (
    <>
      <SearchContainer
        variants={SearchContainerVariants}
        initial={clicked ? "final" : "initial"}
        animate={clicked ? "final" : "initial"}
        ref={ref}
      >
        <SearchInputContainer
          type="text"
          name=""
          onChange={onTextChange}
          placeHolder="Search..."
          variants={searchInputVariants}
          initial={clicked ? "final" : "initial"}
          animate={clicked ? "final" : "initial"}
        />
        <SearchButtonContainer
          whileHover={{ scale: 0.8, rotate: 360 }}
          onClick={() => setClicked(!clicked)}
        >
          <Icon width="44" height="44" viewBox="0 0 44 44">
            <circle cx="26.5" cy="17.5" r="14" stroke="black" strokeWidth="7" />
            <line
              x1="16.7218"
              y1="27.9312"
              x2="2.7218"
              y2="40.9312"
              stroke="black"
              strokeWidth="8"
            />
          </Icon>
        </SearchButtonContainer>
      </SearchContainer>
      {renderSuggestionsList()}
    </>
  );
};
const mapStateToProps = createStructuredSelector({
  SHOP_DATA: selectCollectionForPreview,
});
export default connect(mapStateToProps)(SearchUI);
