import styled, { css } from "styled-components";

export const SearchContainer = styled.div`
  display: flex;
  background-color: #626767;
  height: 40px;
  border-radius: 40px;
  padding: 10px;
`;
export const SearchButtonContainer = styled.div`
  border: none;
  background: none;
  outline: none;
  float: left;
  padding: 0;
  color: white;
  font-size: 16px;
`;
export const SearchInputContainer = styled.input`
  border: none;
  background: none;
  outline: none;
  padding: 0;
  color: white;
  font-size: 25px;
  transition: 0.4s;
  line-height: 40px;
`;
