import styled from "styled-components";

export const UserIconContainer = styled.div`
  height: 50px;
  width: 250px;
  border: solid black;
  display: flex;
`;
export const UserPhotoContainer = styled.div`
  height: 45px;
  width: 45px;
  border-radius: 50%;
  border: 1px solid gray;
  overflow: hidden;
`;
export const UserNameContainer = styled.div`
  height: 100%;
  width: 70%;
  border-radius: 20px;
  display: flex;
  justify-content: space-between;
`;
export const Name = styled.h4`
  margin: 0;
  padding: 0;
  text-align: center;
`;
export const dropdownIcon = styled.svg`
  margin: 0;
  padding: 10px;
`;
