import React, { useState } from "react";
import {
  LogoContainer,
  OptionLink,
  OptionsContainer,
  Optiondiv,
  HeaderContainer,
  LeftSide,
  UserOptionsDropdownContainer,
} from "./Header.Styles";
import { ReactComponent as Logo } from "./crown.svg";
import { connect } from "react-redux";
import Cart from "../cart/cart";
import CartDropDown from "../cartDropDown/cartDropDown";
import { selectCurrentUser } from "../Redux/User/userSelector";
import { selectHidden } from "../Redux/cart/cartSelector";
import { createStructuredSelector } from "reselect";
import { SignOutStart } from "../Redux/User/UserAction";
import UserIcon from "../userIcon/userIcon";
const Header = ({ currentUser, Hidden, signOutStart }) => {
  const [close, setclose] = useState(false);
  const isclosed = (data) => setclose(data);
  console.log(close);
  return (
    <HeaderContainer>
      <LeftSide>
        <LogoContainer to="/">
          <Logo className="logo" />
        </LogoContainer>
        <OptionsContainer>
          <OptionLink to="/shop">Shop</OptionLink>
          <OptionLink to="/shop">Contact</OptionLink>
        </OptionsContainer>
      </LeftSide>
      <OptionsContainer>
        {currentUser ? (
          <>
            <UserIcon signOutStart={signOutStart} />
            <Optiondiv onClick={signOutStart}>SignOut</Optiondiv>
          </>
        ) : (
          <OptionLink to="/Auth">Auth</OptionLink>
        )}
        <Cart />
      </OptionsContainer>
      {Hidden ? null : <CartDropDown />}
      {close ? null : (
        <UserOptionsDropdownContainer>
          {currentUser ? (
            <Optiondiv onClick={signOutStart}>SignOut</Optiondiv>
          ) : null}
        </UserOptionsDropdownContainer>
      )}
    </HeaderContainer>
  );
};
const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  Hidden: selectHidden,
});
const mapDispatchToProps = (dispatch) => ({
  signOutStart: () => dispatch(SignOutStart()),
});
export default connect(mapStateToProps, mapDispatchToProps)(Header);
