import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
const ChouseColorBasedonProps = (props) => {
  switch (props.type) {
    case "facebook":
      return "#4267B2";
    case "github":
      return "#333";
    case "linkedin":
      return "#0A66C2";
    default:
      break;
  }
};
export const ContactContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

export const LinkContainer = styled(motion.Link)`
  will-change: width;
  display: inline-block;
  width: 60px;
  height: 60px;
  margin: 5px 10px;
  background: #fff;
  border-radius: 50px;
  cursor: pointer;
  overflow: hidden;
  box-shadow: 5px 15px 20px 5px rgba(0, 0, 0, 0.55);
`;
export const IconContainer = styled.div`
  height: 60px;
  width: 60px;
  text-align: center;
  border-radius: 50px;
  ${LinkContainer}:hover & {
    background-color: ${ChouseColorBasedonProps};
  }
`;
