import React, { useRef, useState } from "react";
import { AnimateSharedLayout, AnimatePresence } from "framer-motion";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCollectionForPreview } from "../Redux/shop/shopSelector";

import {
  SearchContainer,
  SearchButtonContainer,
  SearchInputContainer,
  Icon,
  SearchDropdown,
  SearchDropdownRow,
} from "./Search.style";
import OutsideClickEvent from "../OutsideClickEvent/OutsideClickEvent";
const searchInputVariants = {
  initial: { width: "0", display: "none" },
  final: { width: "200px", display: "block" },
  transition: {},
};
const SearchContainerVariants = {
  initial: { width: "50px" },
  final: { width: "300px" },
};

const SearchUI = ({ SHOP_DATA }) => {
  console.log("SHOP", SHOP_DATA);
  const [clicked, setClicked] = useState(false);
  const ref = useRef();
  OutsideClickEvent(ref, () => setClicked(false));
  const [suggestions, setSuggestions] = useState([]);
  const search = (query, array) => {
    const filteredList = array
      .filter((e) => e.name.toLowerCase().indexOf(query.toLowerCase()) !== -1)
      .slice(0, 10);
    console.log(
      "filtredList",
      filteredList.map((el) => el.name)
    );
    if (filteredList.length > 0) {
      return filteredList;
    }
  };
  const globalSearch = (query, arrayOfObj) => {
    if (query) {
      const a = arrayOfObj.map((item) => {
        search(query, item.items);
      });
      console.log("a", a);
    } else {
      return [];
    }
  };
  if (SHOP_DATA) {
    globalSearch("a", SHOP_DATA);
  }

  const onTextChange = (e) => {
    let filtredList = [];
    const value = e.target.value;
    let data = [];
    if (value.length > 0) {
      if (SHOP_DATA) {
        const regex = new RegExp(`^${value}`, "i");
        SHOP_DATA.map(({ items, routeName }) =>
          data.push(items.map((item) => item.name))
        );
        let result = [];
        (function () {
          for (let index = 0; index < data.length; index++) {
            result = data[index].filter((v) => regex.test(v) && v != 0).sort();
            if (result.length > 0) {
              filtredList.push(result);
            }
          }
        })();
      }
    }
    setSuggestions([...filtredList.flat()]);
  };
  console.log(suggestions[0]);
  const renderSuggestionsList = () => {
    if (suggestions.length > 0) {
      return (
        <AnimateSharedLayout>
          <AnimatePresence>
            {clicked && (
              <SearchDropdown
                layout
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                {suggestions.map((suggestion) => (
                  <SearchDropdownRow key={suggestion}>
                    {suggestion}
                  </SearchDropdownRow>
                ))}
              </SearchDropdown>
            )}
          </AnimatePresence>
        </AnimateSharedLayout>
      );
    }
    return null;
  };

  return (
    <>
      <SearchContainer
        variants={SearchContainerVariants}
        initial={clicked ? "final" : "initial"}
        animate={clicked ? "final" : "initial"}
        ref={ref}
      >
        <SearchInputContainer
          onChange={(e) => onTextChange(e)}
          placeholder="Search..."
          variants={searchInputVariants}
          initial={clicked ? "final" : "initial"}
          animate={clicked ? "final" : "initial"}
        />
        <SearchButtonContainer
          whileHover={{ scale: 0.8, rotate: 360 }}
          onClick={() => setClicked(!clicked)}
        >
          <Icon width="44" height="44" viewBox="0 0 44 44">
            <circle cx="26.5" cy="17.5" r="14" stroke="black" strokeWidth="7" />
            <line
              x1="16.7218"
              y1="27.9312"
              x2="2.7218"
              y2="40.9312"
              stroke="black"
              strokeWidth="8"
            />
          </Icon>
        </SearchButtonContainer>
      </SearchContainer>
      {renderSuggestionsList()}
    </>
  );
};

const mapStateToProps = createStructuredSelector({
  SHOP_DATA: selectCollectionForPreview,
});
export default connect(mapStateToProps)(SearchUI);
