import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import { Facebook } from "@styled-icons/boxicons-logos/Facebook";

export const ContactContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
`;

export const LinkContainer = styled(motion.Link)`
  padding: 0;
  will-change: width;
  display: flex;
  align-items: center;
  overflow: hidden;
  width: 60px;
  height: 60px;
  margin: 0;
  background: #fff;
  border-radius: 50px;
  cursor: pointer;

  box-shadow: 5px 15px 20px 5px rgba(0, 0, 0, 0.55);
`;
export const FacebookIcon = styled.div`
  height: 60px;
  width: 60px;
  text-align: center;
  border-radius: 50px;
  flex-shrink: 0;
  ${LinkContainer}:hover & {
    background: #3b5998;
  }
`;
export const TwitterIcon = styled(FacebookIcon)`
  ${LinkContainer}:hover & {
    background: #1da1f2;
  }
`;
export const LinkedInIcon = styled(FacebookIcon)`
  ${LinkContainer}:hover & {
    background: #0a66c2;
  }
`;
export const GithubIcon = styled(FacebookIcon)`
  ${LinkContainer}:hover & {
    background: #333;
  }
`;
export const FacebookText = styled.span`
  font-size: 22px;
  font-weight: 900;
  font-family: "Roboto", sans-serif;
  line-height: 60px;
  margin-left: 10px;
  ${LinkContainer}:hover & {
    color: #3b5998;
  }
`;
export const LinkedInText = styled(FacebookText)`
