import styled from "styled-components";

export const UserIconContainer = styled.div`
  position: relative;
  height: 60px;
  width: 250px;
  border: solid black;
  display: flex;
  align-items: center;
`;
export const UserPhotoContainer = styled.div`
  position: absolute;
  left: 0;
  height: 55px;
  width: 55px;
  border-radius: 50%;
  border: 1px solid gray;
  overflow: hidden;
`;
export const UserNameContainer = styled.div`
  margin-left: 30px;
  height: 70%;
  width: 100%;
  border-radius: 20px;
  background-color: #efb4e6;
  display: flex;
  justify-content: space-between;
`;
export const Name = styled.h4`
  margin: 0;
  padding: 0;
  text-align: center;
`;
export const dropdownIcon = styled.svg`
  margin: 0;
  padding: 10px;
`;
