import styled from "styled-components";

export const UserContainer = styled.div`
  position: relative;
  z-index: 6;
  margin: 0;
  height: 500px;
  width: 250px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: solid black;
`;
export const UserIconContainer = styled.div`
  height: 60px;
  width: 250px;
  display: flex;
  align-items: center;
  border: solid red;
`;
export const UserPhotoContainer = styled.div`
  height: 55px;
  width: 55px;
  border-radius: 50%;
  border: 1px solid gray;
  overflow: hidden;
  z-index: 2;
`;
export const UserNameContainer = styled.div`
  margin-left: -30px;
  z-index: 1;
  height: 70%;
  width: 100%;
  border-radius: 20px;
  background-color: #efb4e6;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
`;
export const Name = styled.h4`
  margin: 0 20px;
  padding: 0;
  font-size: 20px;
  text-align: center;
`;
export const CartDropdownContainer = styled.div`
  position: absolute;
  width: 250px;
  display: flex;
  flex-direction: column;
  padding: 20px;
  border: 1px solid black;
  background-color: white;
  top: 90px;
  right: 52px;
  z-index: 5;
`;
