import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
export const ContactContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
`;
export const LinkContainer = styled(motion.Link)`
  display: inline-block;
  width: 60px;
  height: 60px;
  margin: 5px 10px;
  background: #fff;
  border-radius: 50px;
  cursor: pointer;
  overflow: hidden;
  box-shadow: 0 10px 10px rgba(0, 0, 0, 0.1);
`;
export const IconContainer = styled.div`
  background: ${(props) => {
    switch (props.type) {
      case "facebook":
        return "#4267B2";
      case "github":
        return "#333";
      case "linkedin":
        return "#0A66C2";
      default:
        break;
    }
  }};
  height: 60px;
  width: 60px;
  text-align: center;
  border-radius: 50px;
  line-height: 60px;
`;
