import styled, { css } from "styled-components";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

export const ContactContainer = styled.div`
  display: flex;
  justify-content: center;
`;

export const LinkContainer = styled(motion.Link)`
  position: relative;
  padding: 0;
  border: solid black;
  will-change: width;
  display: inline-block;
  width: 60px;
  height: 60px;
  margin: 5px 10px;
  background: #fff;
  border-radius: 50px;
  cursor: pointer;

  box-shadow: 5px 15px 20px 5px rgba(0, 0, 0, 0.55);
`;
export const FacebookIcon = styled.div`
  position: absolute;
  left: 0;
  top: 0;
  height: 60px;
  width: 60px;
  text-align: center;
  border-radius: 50px;
  ${LinkContainer}:hover & {
    background: #4267b2;
  }
`;
export const TwitterIcon = styled(FacebookIcon)`
  ${LinkContainer}:hover & {
    background: #1da1f2;
  }
`;
export const LinkedInIcon = styled(FacebookIcon)`
  ${LinkContainer}:hover & {
    background: #0a66c2;
  }
`;
export const GithubIcon = styled(FacebookIcon)`
  ${LinkContainer}:hover & {
    background: #333;
  }
`;
