import styled, { css } from "styled-components";
import { motion } from "framer-motion";

export const SearchContainer = styled.div`
  margin-top: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #626767;
  height: 50px;
  border-radius: 40px;
  padding: 10px;
  max-width: 20em;
`;
export const SearchButtonContainer = styled.button`
  border: none;
  background: none;
  outline: none;
  padding: 0;
  margin-left: 10px;
  height: 50px;
  width: 50px;
  cursor: pointer;
  text-align: center;
`;
export const SearchInputContainer = styled.input`
  border: none;
  background: none;
  outline: none;
  padding: 0;
  color: white;
  font-size: 25px;
  width: 15rem;
`;
export const Icon = styled(motion.svg)`
  flex: none;
  fill: none;
  width: 30px;
  height: 30px;
`;
